import './MainCard.css'

import Card from 'react-bootstrap/Card'
import Image from '../placeholder-300×225.jpeg'
import React from 'react'

export const MainCard = () => {
    const textForMaincard = "We must hold our organizations accountable where they control \n the doors by demanding clarity and tranparency around \n succession planning. - Ruth Porat, CFO at Alptha";
    const textArranged = ({ textForMaincard }) => {
        const texts = textForMaincard.split(/(\n)/).map((item, index) => {
          return (
            <React.Fragment key={index}>
              { item.match(/\n/) ? <br /> : item }
            </React.Fragment>
          );
        });
        return <div>{texts}</div>;
      }



    return (
        <div>
            <Card className="text-white">
            <Card.Img src={ Image } className='main-img'/>
            <Card.ImgOverlay>
                <Card.Title>Card title</Card.Title>
                <Card.Text>
                This is a wider card with supporting text below as a natural lead-in to
                additional content. This content is a little bit longer.
                </Card.Text>
                <Card.Text>
                    { textForMaincard}
                </Card.Text>
            </Card.ImgOverlay>
            </Card>
        </div>
    )
}

export default MainCard
